package basic;

public class reverse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		        
		        String [] s = new String [] {"str", "gan", "jea"};  
		        
		        for (int i = 0; i < s.length; i++) {  
		            System.out.print(s[i]);  
		        }  
		        System.out.println();  
		        
		         
		        for (int i = s.length-1; i >= 0; i--) {  
		            System.out.print(s[i]);  
		        }  
		    }  
		

	}


